input = ''''''
def func1(s):
    answer = 0
    letters = []
    for i in range(len(s)):
        while s[i] in letters:
            letters.pop(0)
        letters.append(s[i])
        if len(letters) == 4:
            answer = i + 1
            break
    return answer
def func2(s):
    answer = 0
    letters = []
    for i in range(len(s)):
        while s[i] in letters:
            letters.pop(0)
        letters.append(s[i])
        if len(letters) == 14:
            answer = i + 1
            break
    return answer
print(func1(input))
print(func2(input))